const loginLink=document.querySelector(".modal-open-button"),loginPopup=document.querySelector(".modal-connection"),loginClose=loginPopup.querySelector(".modal-close"),loginForm=loginPopup.querySelector(".connection-form"),loginLogin=loginPopup.querySelector(".login-name"),loginEmail=loginPopup.querySelector(".login-email");loginLink.addEventListener("click",function(o){o.preventDefault(),loginPopup.classList.add("modal-show"),loginPopup.classList.remove("modal-error")}),loginClose.addEventListener("click",function(o){o.preventDefault(),loginPopup.classList.remove("modal-show"),loginPopup.classList.remove("modal-error")}),loginForm.addEventListener("submit",function(o){loginLogin.value&&loginEmail.value||(o.preventDefault(),loginPopup.classList.remove("modal-error"),loginPopup.offsetWidth=loginPopup.offsetWidth,loginPopup.classList.add("modal-error"))}),window.addEventListener("keydown",function(o){27===o.keyCode&&loginPopup.classList.contains("modal-show")&&(o.preventDefault(),loginPopup.classList.remove("modal-show"),loginPopup.classList.remove("modal-error"))});
